import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import * as ReactBootStrap from 'react-bootstrap'
import styles from '../styles/modal.module.css'
import Message from './Message.js'
import OTP from './OTP.js'
import io from 'socket.io-client'


const onResponseBack = (socket) => {
    socket.on('response_back', function(image){
        const image_id = document.getElementById('image');
        image_id.src = image;
    });
}

const onResponseUser = (socket) => {
    socket.on('response_user', function(response_user){
        const user = document.getElementById('user')
        user.innerHTML = response_user
    })
}

const onConnect = (socket) => {
    socket.on('connect', function(){
        console.log("Connected...!", socket.connected)
    })
}

const recognizeUser = (socket, video) => {

    const FPS = 22
    const X = 0, Y = 0
    const TIME = 10000

    var canvas = document.getElementById('canvasOutput')
    var context = canvas.getContext('2d')

    canvas.width = 500
    canvas.height = 500

    setInterval(() => {
        context.drawImage(video, X, Y)
        
        var type = "image/png"
        var data = document.getElementById("canvasOutput").toDataURL(type);
        data = data.replace('data:' + type + ';base64,', '');
        socket.emit('image', data);
    }, TIME/FPS);
    
    onResponseBack(socket)
    onResponseUser(socket)

    
}

function Login(){
    const URI = 'http://localhost:5000'

    useEffect(()=>{
        var socket = io(URI)
    
        onConnect(socket)
        
        const video = document.querySelector("#videoElement")
        
        if (navigator.mediaDevices.getUserMedia) {
            navigator.mediaDevices.getUserMedia({ video: true })
            .then(function (stream) {
                video.srcObject = stream;
            })
            .catch(function (err0r) {
                console.log("Something went wrong!");
            });
        }
        
        recognizeUser(socket, video)
    
