import React, {useState, useEffect} from 'react';
import logo from './logo.svg';
import './App.css';
import Greet from './components/Greet'
import Welcome from './components/Welcome'
import Hello from './components/Hello'
import Message from './components/Message'
import Login from './components/Login.js'
import Axios from 'axios';
import * as ReactBootStrap from 'react-bootstrap'
import styles from './styles/modal.module.css'
import io from 'socket.io-client'

const URI = 'http://localhost:5000'



const scriptLoaded = (socket, video) => {


    let globalSrc

    window.cv['onRuntimeInitialized']=()=>{
        let src = new window.cv.Mat(video.height, video.width, window.cv.CV_8UC4);
        globalSrc = src
    };

    let cap = window.cv.VideoCapture(video);
    const FPS = 22;
    
    var canvas = document.getElementById('canvasOutput')
    var context = canvas.getContext('2d')
    canvas.width = 500
    canvas.height = 375

    setInterval(() => {
      
      context.drawImage(video, 0, 0)
      
      var type = "image/png"
      var data = document.getElementById("canvasOutput").toDataURL(type);
      data = data.replace('data:' + type + ';base64,', '');
      socket.emit('image', data);
    }, 10000/FPS);
  
    socket.on('response_back', function(image){
      const image_id = document.getElementById('image');
      image_id.src = image;
    });
}

const mountScript = (socket, video) => {
  const script = document.createElement("script");
  script.src = process.env.PUBLIC_URL + '/opencv.js'
  script.async = true;
  script.onload = () => scriptLoaded(socket, video);

  document.body.appendChild(script);
}



function App() {
  
  useEffect(()=>{
    var socket = io(URI)

    socket.on('connect', function(){
      console.log("Connected...!", socket.connected)
    })
    
    const video = document.querySelector("#videoElement")
    video.width = 500
    video.height = 375

    
    if (navigator.mediaDevices.getUserMedia) {
